## code to prepare `example_fcs` dataset goes here
# The csv was generated by using
#   /data/shared/rhskl1/01_raw/01_FlowCytometryData/UKR_Kaluza/2023-02-23_gated_11-Well-B1_HKP_02/11-Well-B1_HKP_02 C.csv
# Manually removing the first (manually random) events and the last events to get 5000 events
example_csv <- data.table::fread(file.path("data-raw", "example_flowcyto.csv"), sep = ";", dec = ",")

cytobench::export_fcs(
    list("example_fcs" = example_csv),
    safety_scaling = 1, safety_shift = 0,
    outdir = file.path("inst", "extdata"),
    new_colnames_to = "description",
    new_colnames = c(
        "FSC-A" = "no",
        "SSC-A" = "no",
        "FL1-A" = "marker",
        "FL4-A" = "marker",
        "FL5-A" = "marker",
        "FL7-A" = "marker",
        "FL8-A" = "marker",
        "FL9-A" = "marker",
        "FL10-A" = "marker",
        "FL11-A" = "marker",
        "FL12-A" = "marker",
        "FL13-A" = "marker",
        "FL14-A" = "marker",
        "FL16-A" = "marker",
        "FL21-A" = "marker",
        "FSC-Width" = "no",
        "TIME" = "no"
    )
)
